import React, { useState, useEffect } from "react";
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import Routers from "./routers";
import apiRequest from "./api/productApi";
function App() {
  const [products, setProducts] = useState([]);
  // const [status, setStatus] = useState(false);
  // const [color, setColor] = useState("green");
  // // const a = () => {
  // //   setProducts(true);
  // // };
  // Danh sách sản phẩm
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        // console.log(data);
        setProducts(data);
      } catch (error) {
        console.log("failed to request API: ", error);
      }
    };
    getProducts();
  }, []);

  // const onHandleRemove = (id) => {
  //   const newProducts = products.filter((product) => product.id !== id);
  //   const confirmDelete = window.confirm("Bạn có chắc chắn muốn xó không");
  //   console.log(newProducts);
  //   if (confirmDelete) setProducts(newProducts);
  // };
  const onHandleRemove = async (id) => {
    try {
      const { data } = await apiRequest.remove(id);
      const newProducts = products.filter((product) => product.id !== data.id);
      const confirmDelete = window.confirm("Bạn có chắc chắn muốn xó không");
      setProducts(newProducts);
    } catch (error) {
      console.log("failed to request API: ", error);
    }
  };
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product,
    };
    setProducts([...products, newProduct]);
  };
  const onDetail = (id) => {
    const onDetail = products.filter((product) => product.id !== id);
  };
  const onHandleDetail = (product) => {
    console.log(product);
  };
  return (
    <div className="App">
      <Routers
        products={products}
        onRemove={onHandleRemove}
        onAdd={onHanleAdd}
        onDetail={onHandleDetail}
      />
    </div>
  );
}
export default App;
