import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import LayoutMain from "../pages/layouts/LayoutMain";
import LayoutAdmin from "../pages/layouts/LayoutAdmin";
//Admin
import Dashboard from "../pages/views/Admin/Dashboard";
import ProductsManager from "../pages/views/Admin/Products";
//Views
import About from "../pages/views/Main/About";
import Home from "../pages/views/Main/Home";
import Addproduct from "../pages/views/Admin/Addproduct";
import Detail from "../pages/views/Admin/Detail";

const Routers = ({ products, onRemove, onAdd }) => {
  const onHandleRemove = (id) => {
    onRemove(id);
  };
  const onHandleAdd = (product) => {
    onAdd(product);
    //   const newArray = {
    //     id: products.length + 1,
    //     ...product,
    //   };
    //   setProducts([...products, newArray]);
  };
  const onHandleDetail = (product) => {
    console.log(product);
    onDetail(product);
  };
  return (
    <Router>
      <Switch>
        <Route path="/admin/:path?" exact>
          <LayoutAdmin>
            <Switch>
              <Route path="/admin/dashboard" exact>
                <Dashboard />
              </Route>
              <Route path="/admin/products">
                <ProductsManager
                  products={products}
                  onRemove={onHandleRemove}
                />
              </Route>
              <Route path="/admin/add">
                <Addproduct products={products} onAdd={onHandleAdd} />
              </Route>
              <Route path="/admin/detail:id">
                <Detail products={products} onDetail={onHandleDetail} />
              </Route>
            </Switch>
          </LayoutAdmin>
        </Route>
        <Route>
          <LayoutMain>
            <Switch>
              <Route path="/">
                <Home products={products} />
              </Route>
              <Route path="/about">
                <About />
              </Route>
              <Route path="/detail/:id">
                <Detail products={products} />
              </Route>
            </Switch>
          </LayoutMain>
        </Route>
      </Switch>
    </Router>
  );
};

Routers.propTypes = {};

export default Routers;
