import http from "./axiosHttp";

const getAll = () => {
  return http.get("/categories");
};

const getCate = (id) => {
  return http.get(`/categories/${id}`);
};

const create = (data) => {
  return http.post("/products", data);
};

// const update = (id, data) => {
//   return http.put(`/products/${id}`, data);
// };

const removeCate = (id) => {
  console.log(id);
  return http.delete(`/categories/${id}`);
};

export default {
  getAll,
  getCate,
  create,
  removeCate,
};
