import React from "react";
import PropTypes from "prop-types";
import { useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
const AddCate = ({ onCate }) => {
  const { register, handleSubmit, watch, errors } = useForm();
  let history = useHistory();
  // const onSubmit = (data) => console.log(data);
  // const [valueInput, setValueInput] = useState({});
  // const onHandleChange = (dat) => {
  //   //   const { name, value } = e.target;
  //   //   setValueInput({
  //   //     ...valueInput,
  //   //     [name]: value,
  // };
  // setValue({
  //   name: e.target.value,
  // });
  // };
  const onHandleSubmitCate = (data) => {
    // console.log(data);
    onCate(data);
    history.push("/admin/categories");
  };

  return (
    <div className="card shadow mb-4">
      <div className="card-header py-3">
        <h6 className="m-0 font-weight-bold text-primary">Add Category</h6>
      </div>
      <div className="card-body">
        <form onSubmit={handleSubmit(onHandleSubmitCate)}>
          <div className="form-group">
            <label htmlFor="inputCateName">Category Name</label>
            <input
              type="text"
              className="form-control"
              id="inputCateName"
              name="name"
              ref={register({ required: true, minLength: 10 })}
            />
            {errors.name && (
              <span className="text-danger">Mời bạn nhập tên</span>
            )}
            {errors.name && errors.name.type === "minLength" && (
              <span className="text-danger">Mời bạn nhập đủ 5 ký tự</span>
            )}
          </div>

          <div className="form-group">
            <label htmlFor="inputCateName">Detail</label>
            <input
              type="text"
              className="form-control"
              id="inputCateName"
              name="description"
              ref={register({ required: true, minLength: 20 })}
            />
            {errors.description && (
              <span className="text-danger">Mời bạn nhập chi tiết</span>
            )}
            {errors.description && errors.detail.type === "minLength" && (
              <span className="text-danger">Mời bạn nhập đủ 20 ký tự</span>
            )}
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

AddCate.propTypes = {};

export default AddCate;
