import React from "react";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
// product: { name, price, status }
const ProductsManager = ({ products, onRemove }) => {
  console.log(products);
  //   console.log(props.product.name);
  // const name =props.product.name;
  // const status =props.product.status;
  // const{name,price,status}=props.product;
  //khai báo 1 biến là 1 mảng chứa nhiều object
  //Truyền dữ liệu xuống component Product thông qua props
  //dựa vào props, sử dụng .map để hiểu thị dữ liệu
  const onHandleRemove = (id) => {
    onRemove(id);
  };

  return (
    <div>
      {/* Page Heading */}
      <h1 className="h3 mb-2 text-gray-800">Quản lý sản phẩm</h1>
      {/* DataTales Example */}
      <div className="card shadow mb-4">
        <div className="card-body">
          <div className="table-responsive">
            <Link to="/add" className=" btn btn-primary">
              Thêm sản phẩm
            </Link>

            <table
              className="table table-bordered"
              id="dataTable"
              width="100%"
              cellSpacing={0}
            >
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Name</th>
                  <th>Image</th>
                  <th>Price</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {products.map(({ id, name, image, price }, index) => (
                  <tr key={index}>
                    <td>{index}</td>
                    <td>{name}</td>
                    <td>
                      <img src={image} width="100"></img>
                    </td>
                    <td>{price}</td>

                    <td>
                      <Link
                        to={"/detail/" + id}
                        className="nav-link"
                        className="btn btn-primary"
                      >
                        Detail
                      </Link>
                      <button
                        className="btn btn-danger"
                        onClick={() => onHandleRemove(id)}
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
    // <div className="container">
    //   <div className="row">
    //     {products.map((product, index) => (
    //       <div className="col" key={index}>
    //         <div className="card">
    //           {/* <h4>{name}</h4>
    //   <h4>{price}</h4>
    //   <h4>{status ? "con hàg" : "het hang"}</h4> */}

    //           <div>
    //             <img
    //               className="card-img-top"
    //               src="https://gamek.mediacdn.vn/2019/12/27/photo-1-15774615977811373426895.jpg"
    //               alt="Card image cap"
    //             ></img>
    //             <div className="card-body">
    //               <p className="card-text">{product.name}</p>
    //             </div>
    //             {/* <p>{product.status ? "con hàg" : "het hang"}</p> */}
    //             <a href="#" class="btn btn-primary">
    //               Go somewhere
    //             </a>
    //           </div>
    //         </div>
    //       </div>
    //     ))}
    //   </div>
    // </div>
  );
};

ProductsManager.propTypes = {};

export default ProductsManager;
