import React, { useState } from "react";
import PropTypes from "prop-types";
import { useForm } from "react-hook-form";
import dataFake from "../../../../dataFake";
const Addproduct = ({ onAdd }) => {
  const { register, handleSubmit, watch, errors } = useForm();
  const onSubmit = (data) => console.log(data);
  // const [valueInput, setValueInput] = useState({});
  // const onHandleChange = (dat) => {
  //   //   const { name, value } = e.target;
  //   //   setValueInput({
  //   //     ...valueInput,
  //   //     [name]: value,
  // };
  // setValue({
  //   name: e.target.value,
  // });
  // };
  const onHandleSubmit = (data) => {
    console.log(data);
    // e.preventDefault();
    onAdd(data);
  };

  // const Addproduct = (props) => {
  return (
    <div className="card shadow mb-4">
      <div className="card-header py-3">
        <h6 className="m-0 font-weight-bold text-primary">Add Product</h6>
      </div>
      <div className="card-body">
        <form onSubmit={onHandleSubmit(onSubmit)}>
          <div className="form-group">
            <label htmlFor="inputProductName">Product Name</label>
            <input
              type="text"
              className="form-control"
              id="inputProductName"
              name="name"
              ref={register({ required: true })}
            />
            {errors.name && <span>This field is required</span>}
          </div>
          <div className="form-group">
            <label htmlFor="inputProductImage">Image</label>
            <input
              type="text"
              className="form-control"
              id="inputProductImage"
              name="image"
            />
          </div>
          <div className="form-group">
            <label htmlFor="inputProductPrice">Price</label>
            <input
              type="text"
              className="form-control"
              id="inputProductPrice"
              name="price"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

Addproduct.propTypes = {};

export default Addproduct;
