import React, { useState } from "react";
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from "./dataFake";
import Routers from "./routers";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Addproduct from "./pages/views/Admin/Addproduct";
// import AddProduct from './components/AddProduct';
function App() {
  const [products, setProduct] = useState(dataFake);
  console.log(products);
  // const [isMarried, setProduct1] = useState(dataFake);
  const [color, setColor] = useState("green");
  const onClickOn = () => {
    setColor("red");
  };
  const a = () => {
    setProduct(true);
  };
  const onHandleRemove = (id) => {
    console.log(products);
    const newArray = products.filter((product) => product.id !== id);
    const confirmDelete = window.confirm("Bạn có chắc chắn muốn xó không");
    console.log(newArray);
    if (confirmDelete) setProduct(newArray);
  };
  const onHandleAdd = (product) => {
    const newArray = {
      id: products.length + 1,
      ...product,
    };
    setProduct([...products, newArray]);
    console.log(product);
  };
  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} />
    </div>
  );
}
export default App;
