  //   setProducts([...products, product]);
  //   //Spread syntax (...)
  // };

  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} />
      {/* <Routers onAdd={onHandleAdd} /> */}
    </div>
  );
}
export default App;
