import React, { useState } from "react";
import PropTypes from "prop-types";
import { useForm } from "react-hook-form";
const Addproduct = ({ onAdd }) => {
  const [valueInput, setValueInput] = useState({});
  const onHandleChange = (e) => {
    const { name, value } = e.target;
    setValueInput({
      ...valueInput,
      [name]: value,
    });
    // setValue({
    //   name: e.target.value,
    // });
  };
  const onHandleSubmit = (e) => {
    e.preventDefault();
    onAdd(valueInput);
  };

  // const Addproduct = (props) => {
  return (
    <div className="card shadow mb-4">
      <div className="card-header py-3">
        <h6 className="m-0 font-weight-bold text-primary">Add Product</h6>
      </div>
      <div className="card-body">
        <form onSubmit={onHandleSubmit}>
          <div className="form-group">
            <label htmlFor="inputProductName">Product Name</label>
            <input
              type="text"
              className="form-control"
              id="inputProductName"
              onChange={onHandleChange}
              name="name"
            />
          </div>
          <div className="form-group">
            <label htmlFor="inputProductImage">Image</label>
            <input
              type="text"
              className="form-control"
              id="inputProductImage"
              onChange={onHandleChange}
              name="image"
            />
          </div>
          <div className="form-group">
            <label htmlFor="inputProductPrice">Price</label>
            <input
              type="text"
              className="form-control"
              id="inputProductPrice"
              onChange={onHandleChange}
              name="price"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

Addproduct.propTypes = {};

export default Addproduct;
