  const onHandleRemove = (id) => {
    const newProducts = products.filter((product) => product.id !== id);
    const confirmDelete = window.confirm("Bạn có chắc chắn muốn xó không");
    console.log(newProducts);
    if (confirmDelete) setProducts(newProducts);
  };
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product,
    };
    setProducts([...products, newProduct]);
  };
  const onDetail = (id) => {
    const onDetail = products.filter((product) => product.id !== id);
  };
  const onHandleDetail = (product) => {
    console.log(product);
  };
  return (
    <div className="App">
      <Routers
        products={products}
        onRemove={onHandleRemove}
        onAdd={onHanleAdd}
        onDetail={onHandleDetail}
      />
    </div>
  );
}
export default App;
