import React, { useState } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import LayoutMain from "../pages/layouts/LayoutMain";
import LayoutAdmin from "../pages/layouts/LayoutAdmin";
//Admin
import Dashboard from "../pages/views/Admin/Dashboard";
import ProductsManager from "../pages/views/Admin/Products";
//Views
import About from "../pages/views/Main/About";
import Men from "../pages/views/Main/Men";
import Home from "../pages/views/Main/Home";
import Addproduct from "../pages/views/Admin/Addproduct";
import Detail from "../pages/views/Main/Detail";
import AddDetail from "../pages/views/Admin/AddDetail";
import Contact from "../pages/views/Main/Contact";
import Cart from "../pages/views/Main/Cart";
import EditProduct from "../pages/views/Admin/EditProduct";
import Categories from "../pages/views/Admin/Categories";
import AddCate from "../pages/views/Admin/AddCate";
const Routers = ({
  products,
  onRemove,
  onAdd,
  onUpdate,
  categories,
  onRemoveCate,
  onAddCate,
}) => {
  const onHandleRemove = (id) => {
    onRemove(id);
  };
  const onHandleAdd = (product) => {
    onAdd(product);
    //   const newArray = {
    //     id: products.length + 1,
    //     ...product,
    //   };
    //   setProducts([...products, newArray]);
  };
  const onHandleUpdate = (id, product) => {
    onUpdate(id, product);
  };
  //danh muc
  const onHandleRemoveCate = (id) => {
    onRemoveCate(id);
  };
  const onHandleCate = (cate) => {
    onAddCate(cate);
  };
  return (
    <Router>
      <Switch>
        <Route path="/admin/:path?">
          <LayoutAdmin>
            <Switch>
              <Route path="/admin/dashboard">
                <Dashboard />
              </Route>
              <Route path="/admin/products">
                <ProductsManager
                  products={products}
                  onRemove={onHandleRemove}
                />
              </Route>
              <Route path="/admin/add">
                <Addproduct products={products} onAdd={onHandleAdd} />
              </Route>
              <Route path="/admin/AddDetail/:id">
                <AddDetail products={products} />
              </Route>
              <Route path="/admin/edit/:id">
                <EditProduct products={products} onUpdate={onHandleUpdate} />
              </Route>
            </Switch>

            <Route path="/admin/categories">
              <Categories
                categories={categories}
                onRemoveCate={onHandleRemoveCate}
              />
            </Route>
            <Route path="/admin/addcategory">
              <AddCate categories={categories} onCate={onHandleCate} />
            </Route>
          </LayoutAdmin>
        </Route>
        <Route>
          <LayoutMain>
            <Switch>
              <Route path="/" exact>
                <Home products={products} />
              </Route>
              <Route path="/detail/:id">
                <Detail products={products} />
              </Route>
              <Route path="/about">
                <About />
              </Route>
              <Route path="/men">
                <Men products={products} />
              </Route>
              <Route path="/contact">
                <Contact />
              </Route>
              <Route path="/cart">
                <Cart />
              </Route>
            </Switch>
          </LayoutMain>
        </Route>
      </Switch>
    </Router>
  );
};

Routers.propTypes = {};

export default Routers;
