import React, { useState, useEffect } from "react";
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import Routers from "./routers";
import apiRequest from "./api/productApi";
function App() {
  const [products, setProducts] = useState([]);
  const [categories, setCategory] = useState([]);
  // const [status, setStatus] = useState(false);
  // const [color, setColor] = useState("green");
  // // const a = () => {
  // //   setProducts(true);
  // // };
  // Danh sách sản phẩm
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log("Lỗi: ", error);
      }
    };
    getProducts();
  }, []);
  // Xóa sản phẩm
  const onHandleRemove = async (id) => {
    try {
      const { data } = await apiRequest.remove(id);
      const newProducts = products.filter((product) => product.id !== data.id);
      const confirmDelete = window.confirm("Bạn có chắc chắn muốn xó không");
      console.log(newProducts);
      if (confirmDelete) setProducts(newProducts);
    } catch (error) {
      console.log("Lỗi: ", error);
    }
  };
  //Thêm Sản phẩm
  const onHanleAdd = async (product) => {
    try {
      const { data } = await apiRequest.create(product);
      // const newProduct = {
      //   id: products.length + 1,
      //   ...product,
      // };
      setProducts([...products, data]);
    } catch (error) {
      console.log("Lỗi: ", error);
    }
  };
  //Sửa Sản phẩm
  const onHandleUpdate = (updateProduct) => {
    const newProducts = products.map(
      (product) => (product.id === updateProduct.id ? updateProduct : product) // Nếu product.id bằng với id của sản phẩm vừa chỉnh sửa thì trả về mảng có object mới
    );
    localStorage.setItem("products", JSON.stringify(newProducts));
    setProducts(newProducts);
  };
  //Danh mục sản phẩm

  useEffect(() => {
    const getCategory = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setCategory(data);
      } catch (error) {
        console.log("Lỗi: ", error);
      }
    };
    getCategory();
  }, []);
  return (
    <div className="App">
      <Routers
        products={products}
        categories={categories}
        onRemove={onHandleRemove}
        onAdd={onHanleAdd}
        onUpdate={onHandleUpdate}
      />
    </div>
  );
}
export default App;
