import React, { useState } from "react";
import Routers from "./routers";
import apiRequest from "./api/productApi";
import axios from "axios";

function App() {
  const [products, setProducts] = useState();
  // const [status, setStatus] = useState(false);
  // const [color, setColor] = useState("green");
  // // const a = () => {
  // //   setProducts(true);
  // // };
  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log("failed to request API: ", error);
      }
    };
    getProducts();
  }, []);
  const onHandleRemove = (id) => {
    const newProducts = products.filter((product) => product.id !== id);
    const confirmDelete = window.confirm("Bạn có chắc chắn muốn xó không");
    console.log(newProducts);
    if (confirmDelete) setProducts(newProducts);
  };
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product,
    };
    setProducts([...products, newProduct]);
  };
  const onDetail = (id) => {
    const onDetail = products.filter((product) => product.id !== id);
  };
  const onHandleDetail = (product) => {
    console.log(product);
  };
  return (
    <div className="App">
      <Routers
        products={products}
        onRemove={onHandleRemove}
        onAdd={onHanleAdd}
        onDetail={onHandleDetail}
      />
    </div>
  );
}
export default App;
