import React, { useState } from "react";
import PropTypes from "prop-types";
import { useParams, useHistory } from "react-router-dom";
import { useForm } from "react-hook-form";
import firebase from "../../../../firebase";
const EditProduct = ({ products, onUpdate }) => {
  const { register, handleSubmit, errors } = useForm();
  let { id } = useParams();
  let history = useHistory();
  const product = products.find((product) => product.id == id);

  const [currentProduct, setCurrentProduct] = useState(product);

  const onHandleSubmit = (e) => {
    e.preventDefault();
    onUpdate(currentProduct);
    history.push("/admin/products");
  };
  const onHandleChange = (e) => {
    const { name, value } = e.target;
    setCurrentProduct({
      ...currentProduct,
      [name]: value,
    });
  };
  return (
    <div>
      <form action="" onSubmit={onHandleSubmit} className="w-50">
        <div className="form-group">
          <label htmlFor="productName">Tên sản phẩm</label>
          <input
            type="text"
            name="name"
            value={currentProduct.name}
            onChange={onHandleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="productName">Ảnh</label>
          <input
            type="text"
            name="image"
            value={currentProduct.image}
            onChange={onHandleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="productName">Giá sản phẩm</label>
          <input
            type="text"
            name="price"
            value={currentProduct.price}
            onChange={onHandleChange}
            className="form-control"
          />
        </div>
        <div className="form-group">
          <label htmlFor="productName">Chi tiết sản phẩm</label>
          <input
            type="text"
            name="detail"
            value={currentProduct.detail}
            onChange={onHandleChange}
            className="form-control"
          />
        </div>
        <button className="btn btn-primary">Cập nhật</button>
      </form>
    </div>
  );
};

EditProduct.propTypes = {};

export default EditProduct;
